  $series_id = get_the_ID();
  $series_status = get_post_status( $series_id );
  $series_title = get_the_title( $series_id );
  $series_date = get_the_date( 'F jS, Y', $series_id );
  $series_future_date = get_the_date( 'M. j', $series_id );
  $series_authors = get_field( 'authors_contributors', $series_id );
  $series_excerpt = wp_trim_words( get_the_content( $series_id ), 75, '' ) . '';
  $series_image = get_field( 'hub_image', $series_id );
  $series_link = get_the_permalink( $series_id );
  $series_more_ways = get_field( 'podcast_urls', $series_id );
  $series_series = get_field( 'series', $series_id );
  $series_runtime = get_field( 'runtime', $series_id );

  $formatted_series = '';
  if ( $series_series['season'] ) {
    $formatted_series .= 'Season ' . $series_series['season'] . ', ';
  }
  if ( $series_series['episode'] ) {
    $formatted_series .= 'Episode ' . $series_series['episode'];
  }

  $formatted_time = '';
  if ( $series_runtime['hours'] ) {
    $formatted_time .= $series_runtime['hours'] . ':';
  }
  if ( $series_runtime['minutes'] ) {
    if ( substr( $formatted_time, -1 ) == ':' && $series_runtime['minutes'] < 10 ) {
      $formatted_time .= '0';
    }
    if ( $series_runtime['minutes'] == 0 ) {
      $formatted_time .= '0:';
    } else {
      $formatted_time .= $series_runtime['minutes'] . ':';
    }
  } elseif ( substr( $formatted_time, -1 ) == ':' ) {
    $formatted_time .= '00:';
  }
  if ( $series_runtime['seconds'] ) {
    if ( substr( $formatted_time, -1 ) == ':' ) {
      if ( $series_runtime['seconds'] < 10 ) {
        $formatted_time .= '0';
      }
      if ( $series_runtime['seconds'] == 0 ) {
        $formatted_time .= '0';
      } else {
        $formatted_time .= $series_runtime['seconds'];
      }
    } else {
      $formatted_time .= $series_runtime['seconds'] . ' seconds';
    }
  } elseif ( substr( $formatted_time, -1 ) == ':' ) {
    $formatted_time .= '00';
  }
?>

<li class="series__card" data-season="<?php echo $series_series['season']; ?>" data-episode="<?php echo $series_series['episode']; ?>" style="order: <?php echo $series_series['episode']; ?>;">
  <div class="series__image">
    <?php if ( $series_image ) : ?><img src="<?php echo esc_url( $series_image['url'] ); ?>" alt="" loading="lazy"/><?php endif; ?>
  </div>

  <?php if ( $series_status == 'future' ) : ?>
    <p class="series__upcoming"><?php echo __('upcoming episode', 'mayo'); ?></p>
  <?php endif; ?>

  <p class="series__series"><?php echo $formatted_series; ?></p>
  <p class="series__title"><?php echo $series_title; ?></p>

  <?php if ( $series_status != 'future' ) : ?>
    <div class="series__meta">
      <time class="series__date">
        <?php echo $series_date; ?>
      </time>
      <?php if ( $formatted_time != '' ) : ?>
        <span class="series__runtime">
          <svg class="icon icon--clock" aria-hidden="true" focusable="false">
            <use xlink:href="#clock"></use>
          </svg>
          <span class="sr-only">Runtime:</span>
          <?php echo $formatted_time; ?>
        </span>
      <?php endif; ?>
    </div>
  <?php endif; ?>

  <?php if ( $series_authors ) : ?>
    <div class="series__authors is-hidden--tablet-down">
      <?php foreach( $series_authors as $author ) : ?>
        <?php
          $author_name = get_the_title( $author->ID );
          $author_excerpt = get_field( 'author_hub', $author->ID );
        ?>
        <p><strong><?php echo $author_name; ?></strong> <?php echo $author_excerpt; ?></p>
      <?php endforeach; ?>
    </div>
  <?php endif; ?>

  <div class="series__summary">
    <?php if ( $series_status != 'future' ) : ?>
      <input type="checkbox" id="summary<?php echo $series_series['season'] . $series_series['episode']; ?>">
      <div class="series__summary--accordion-content">
        <?php if ( $series_authors ) : ?>
          <div class="series__authors is-hidden--desktop">
            <?php foreach( $series_authors as $author ) : ?>
              <?php
                $author_name = get_the_title( $author->ID );
                $author_excerpt = get_field( 'author_hub', $author->ID );
              ?>
              <p><strong><?php echo $author_name; ?></strong> <?php echo $author_excerpt; ?></p>
            <?php endforeach; ?>
          </div>
        <?php endif; ?>

        <p class="series__summary--content"><?php echo $series_excerpt; ?></p>
      </div>
      <label class="series__summary--toggle" for="summary<?php echo $series_series['season'] . $series_series['episode']; ?>">
        <span class="sr-only">Episode Summary</span>
        <svg class="icon icon--chevron-down" aria-hidden="true" focusable="false">
          <use xlink:href="#chevron-down"></use>
        </svg>
      </label>
    <?php endif; ?>
  </div>

  <?php if ( $series_status == 'future' ) : ?>
    <?php printf( '<p class="series__coming-soon"><strong>%s</strong> %s</p>', 'Coming Soon!', 'Available ' . $series_future_date ); ?>
  <?php else : ?>
    <div class="series__links">
      <a href="<?php echo esc_url( $series_link ); ?>" class="btn btn--primary">
        <svg class="icon icon--play-circle icon--md" aria-hidden="true" focusable="false">
          <use xlink:href="#play-circle"></use>
        </svg>
        <?php echo __( 'Listen Now', 'mayo' ); ?>
      </a>

      <?php if ( $series_more_ways ) : ?>
        <input type="checkbox" id="adtl-links<?php echo $series_series['season'] . $series_series['episode']; ?>">
        <label class="series__links--toggle" for="adtl-links<?php echo $series_series['season'] . $series_series['episode']; ?>">
          <span class="sr-only">More Ways to Listen</span>
          <svg class="icon icon--chevron-down" aria-hidden="true" focusable="false">
            <use xlink:href="#chevron-down"></use>
          </svg>
        </label>

        <div class="series__links--adtl">
          <?php foreach ( $series_more_ways as $series_platform ) : ?>
            <div class="series__links--content">
              <a href="<?php echo esc_url( $series_platform['podcast_url'] ); ?>">
                <?php
                  switch ( $series_platform['podcast_platform'] ) {
                    case 'apple-podcast':
                      echo $apple_podcast_svg;
                      break;
                    case 'spotify-podcast':
                      echo $spotify_podcast_svg;
                      break;
                    case 'google-podcast':
                      echo $google_podcast_svg;
                      break;
                  };
                ?>
              </a>
            </div>
          <?php endforeach; ?>
        </div>
      <?php endif; ?>
    </div>
  <?php endif; ?>
</li>
